import { useAppDispatch, useAppSelector } from "../app/hooks";
import { addItem } from "../features/cart/cartSlice";
import { selectProducts } from "../features/cart/cartSelectors";

export default function ProductList() {
  const products = useAppSelector(selectProducts);
  const dispatch = useAppDispatch();

  return (
    <div className="p-4 bg-white shadow rounded w-80">
      <h2 className="text-2xl font-bold mb-4">Products</h2>
      {products.map((p) => (
        <div key={p.id} className="flex justify-between py-2 border-b items-center">
          <span>{p.name}</span>
          <span>Â£{p.price.toFixed(2)}</span>
          <button
            onClick={() => dispatch(addItem(p.id))}
            className="ml-2 px-3 py-1 bg-blue-500 text-white rounded"
          >
            Add
          </button>
        </div>
      ))}
    </div>
  );
}
